mapper.update:
update tb_board
set view_count = view_count + 1
where id = #{id}

update tb_board
set status = 'N'
where id = #{id}

update tb_board
set title = #{title},
		content = #{content},
		modify_date = sysdate,
		image_name = #{imageName}
where id = #{id}

